<?phpnamespace LinkSoft\Sms\Adapter;use GuzzleHttp\Exception\GuzzleException;use Hyperf\Utils\Codec\Json;use LinkSoft\Sms\Driver;/** * 创蓝通道 * Class Sms253 * @package LinkSoft\Sms */class SmsDev extends Driver{    public function send(string $country, string $mobile, string $content): bool    {        // TODO: Implement send() method.        $data = [];        $data['msgtype'] = 'text';        $data['text'] = ['content' => '【应用名称】' . env('APP_NAME', 'LinkTrade') . "\r\n【提示消息】" . $content];        $data['isAtAll'] = true;        try {            $response = $this->client->post($this->options['api'], [                'json' => $data,            ]);            $result = $response->getBody()->getContents();            $result = Json::decode($result);            if (isset($result['errcode']) && $result['errcode'] == 0) {                return true;            }            $this->logger->error('send dev sms error ' . $result['errmsg']);            return false;        } catch (GuzzleException $exception) {            $this->logger->error('send dev sms error ' . $exception->getMessage());            return false;        }    }}