<?phpnamespace LinkSoft\Sms\Adapter;use GuzzleHttp\Exception\GuzzleException;use LinkSoft\Sms\Driver;/** * LinkSoft 通道 * Class SmsLink * @package LinkSoft\Sms */class SmsLink extends Driver{    public function send(string $country, string $mobile, string $content): bool    {        // TODO: Implement send() method.        try {            $data = [];            $data['SpCode'] = $this->options['code'];            $data['LoginName'] = $this->options['username'];            $data['Password'] = $this->options['password'];            $data['MessageContent'] = preg_replace('/^【.*】/', '', $content);            $data['MessageType'] = 2;            $data['UserNumber'] = $mobile;            $data['f'] = 1;            $data['AutographId'] = $this->options['sign'];            $response = $this->client->post($this->options['api'] . '/api/send', [                'form_params' => $data            ]);            $response = $response->getBody()->getContents();            parse_str($response, $result);            if ($result['result'] != 0) {                $this->logger->error('send link sms error ' . $result['description']);                return false;            }            return true;        } catch (GuzzleException $exception) {            $this->logger->error('send link sms error ' . $exception->getMessage());            return false;        }    }}