<?phpnamespace LinkSoft\Sms;use GuzzleHttp\Client;use Hyperf\Contract\StdoutLoggerInterface;use Hyperf\Guzzle\HandlerStackFactory;use Hyperf\Utils\ApplicationContext;abstract class Driver{    /**     * @var Client     */    protected $client;    /**     * @var array     */    protected $options = [];    /**     * @var StdoutLoggerInterface|mixed     */    protected $logger;    public function __construct(array $options = [])    {        $container = ApplicationContext::getContainer();        $factory = new HandlerStackFactory();        $stack = $factory->create();        $this->client = make(Client::class, [            'config' => [                'handler' => $stack,            ]        ]);        $this->options = $options;        $this->logger = $container->get(StdoutLoggerInterface::class);    }    abstract public function send(string $country, string $mobile, string $content): bool;}