<?phpnamespace LinkSoft\Sms;use Hyperf\Contract\ConfigInterface;use LinkSoft\Sms\Exception\ConfigException;use Psr\Container\ContainerInterface;class SmsFactory{    /**     * @var ContainerInterface     */    protected $container;    /**     * @var ConfigInterface     */    protected $config;    /**     * @var array     */    private $channels = [];    public function __invoke(ContainerInterface $container)    {        $this->container = $container;        $this->config = $container->get(ConfigInterface::class);    }    public function get(string $name = 'default')    {        if (isset($this->channels[$name]) && $this->channels[$name] instanceof Driver) {            return $this->channels[$name];        }        return $this->channels[$name] = $this->make($name);    }    public function make(string $name = 'default'): Driver    {        if (!$options = $this->config->get('sms.' . $name)) {            throw new ConfigException('sms config empty');        }        $driver = $options['drive'] ?? 'default';        return make($driver, ['options' => $options]);    }}