<?phpnamespace LinkSoft\Sms;use Hyperf\Contract\ConfigInterface;use LinkSoft\Sms\Exception\ConfigException;use Psr\Container\ContainerInterface;class SmsFactory{    /**     * @var ContainerInterface     */    private $container;    /**     * @var ConfigInterface     */    private $config;    /**     * @var array     */    private $channels = [];    public function __invoke(ContainerInterface $container)    {        $this->container = $container;        $this->config = $container->get(ConfigInterface::class);    }    public function get(string $name = 'default', $options = [])    {        if (isset($this->channels[$name]) && $this->channels[$name] instanceof Driver) {            return $this->channels[$name];        }        return $this->channels[$name] = $this->make($name, $options);    }    private function make(string $name = 'default', array $options = []): Driver    {        if (!$config = $this->config->get('sms.' . $name)) {            throw new ConfigException('sms config empty');        }        $options = array_merge($config, $options);        $driver = $options['drive'] ?? 'default';        return make($driver, ['options' => $options]);    }}